// This entry need to be wrapped in an IIFE because it need to be isolated against other entry modules.
(() => {
function main(){

    function tabChange(){
        $('.ggc-header-box-tab >li').on('click',(e)=>{
            $(e.target).addClass('active').siblings().removeClass('active')
        })
    }

    function arcodian(){
        $('.ggc-arcodian-wrap>li .desc').hide();
        $('.ggc-arcodian-wrap>li').on('click',(event)=>{

            if($(event.currentTarget).hasClass('active')){
                $(event.currentTarget).removeClass('active')
                $(event.currentTarget).children('.desc').stop();
                $(event.currentTarget).children('.desc').slideUp();
            } else{
                $(event.currentTarget).addClass('active')
                $(event.currentTarget).children('.desc').stop();
                $(event.currentTarget).children('.desc').slideDown();
            }
        })
    }
    arcodian();
    tabChange();
}
window.addEventListener('compFuncRun', main);
})();

